function [c,ceq]=fun2(x)
%非线性约束
B=[0.9 1.8 2.3 1.2 2.1 2.8];
ceq=[];

c(1)=((x(1)*(x(1)/(16*B(1)^2*pi) - 1))/(16*B(1)^2*pi) + (x(2)*(x(2)/(16*B(2)^2*pi) - 1))/(16*B(2)^2*pi) + (x(3)*(x(3)/(16*B(3)^2*pi) - 1))/(16*B(3)^2*pi) + (x(4)*(x(4)/(16*B(4)^2*pi) - 1))/(16*B(4)^2*pi) + (x(5)*(x(5)/(16*B(5)^2*pi) - 1))/(16*B(5)^2*pi) + (x(6)*(x(6)/(16*B(6)^2*pi) - 1))/(16*B(6)^2*pi))/((x(1)/(16*B(1)^2*pi) + x(2)/(16*B(2)^2*pi) + x(3)/(16*B(3)^2*pi) + x(4)/(16*B(4)^2*pi) + x(5)/(16*B(5)^2*pi) + x(6)/(16*B(6)^2*pi))*(x(1)/(16*B(1)^2*pi) + x(2)/(16*B(2)^2*pi) + x(3)/(16*B(3)^2*pi) + x(4)/(16*B(4)^2*pi) + x(5)/(16*B(5)^2*pi) + x(6)/(16*B(6)^2*pi) - 1))-0.3;

c(2)=20-((5*x(1))/(8*B(1)^2*pi) + (5*x(2))/(4*B(2)^2*pi) + (15*x(3))/(8*B(3)^2*pi) + (5*x(4))/(8*B(4)^2*pi) + (5*x(5))/(4*B(5)^2*pi) + (15*x(6))/(8*B(6)^2*pi))/(x(1)/(16*B(1)^2*pi) + x(2)/(16*B(2)^2*pi) + x(3)/(16*B(3)^2*pi) + x(4)/(16*B(4)^2*pi) + x(5)/(16*B(5)^2*pi) + x(6)/(16*B(6)^2*pi));
